/**
 * ProductCard Component
 * Reusable product card for displaying product information
 */
import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const { addToCart, addToWishlist } = useCart();

  const handleAddToCart = (e) => {
    e.preventDefault();
    addToCart(product);
  };

  const handleAddToWishlist = (e) => {
    e.preventDefault();
    addToWishlist(product);
  };

  return (
    <div className="product-card">
      <div className="product-item border h-100">
        <div className="position-relative">
          <img
            src="/img/product-1.png"
            className="img-fluid w-100"
            alt={product.product_name}
            style={{ height: '250px', objectFit: 'cover', backgroundColor: '#f8f9fa' }}
          />
          <div className="product-overlay">
            <Link
              to={`/single/${product.product_id}`}
              className="btn btn-sm btn-primary rounded-circle"
            >
              <i className="fa fa-eye"></i>
            </Link>
          </div>
        </div>

        <div className="product-content p-4">
          {product.category && (
            <Link to={`/shop?category=${product.category.category_name}`} className="d-block mb-2 text-muted small">
              {product.category.category_name}
            </Link>
          )}
          <Link to={`/single/${product.product_id}`} className="h6 d-block mb-3 text-dark">
            {product.product_name}
          </Link>

          {product.description && (
            <p className="text-muted small mb-3" style={{ 
              overflow: 'hidden', 
              textOverflow: 'ellipsis', 
              display: '-webkit-box', 
              WebkitLineClamp: 2, 
              WebkitBoxOrient: 'vertical',
              minHeight: '40px'
            }}>
              {product.description}
            </p>
          )}

          <div className="d-flex gap-2 mt-3">
            <button
              onClick={handleAddToCart}
              className="btn btn-primary rounded-pill flex-grow-1"
            >
              <i className="fas fa-shopping-cart me-2"></i>
              Add to Cart
            </button>
            <button
              onClick={handleAddToWishlist}
              className="btn btn-outline-primary rounded-circle"
              title="Add to Wishlist"
            >
              <i className="fas fa-heart"></i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
