/**
 * API Configuration
 * Centralized API endpoint configuration
 */

const API_BASE_URL = 'http://127.0.0.1:8020';

export const API_ENDPOINTS = {
  // Products
  PRODUCTS: `${API_BASE_URL}/products`,
  PRODUCT_BY_ID: (id) => `${API_BASE_URL}/products/${id}`,
  PRODUCT_VARIANTS: (id) => `${API_BASE_URL}/products/${id}/variants`,

  // Categories
  CATEGORIES: `${API_BASE_URL}/categories`,
  CATEGORY_BY_ID: (id) => `${API_BASE_URL}/categories/${id}`,

  // Cart
  CART: `${API_BASE_URL}/cart`,
  CART_ITEM: (itemId) => `${API_BASE_URL}/cart/${itemId}`,

  // Auth
  LOGIN: `${API_BASE_URL}/auth/login`,
  REGISTER: `${API_BASE_URL}/auth/register`,
  LOGOUT: `${API_BASE_URL}/auth/logout`,

  // User
  USER_PROFILE: `${API_BASE_URL}/users/me`,

  // Contact
  CONTACT: `${API_BASE_URL}/contact`,

  // Location
  PROVINCES: `${API_BASE_URL}/locations/provinces`,
  DISTRICTS: (provinceId) => `${API_BASE_URL}/locations/provinces/${provinceId}/districts`,
  CITIES: (districtId) => `${API_BASE_URL}/locations/districts/${districtId}/cities`,
};

export default API_BASE_URL;
