/**
 * Shop Page Component
 * Product listing page with filters and sorting
 */
import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';
import ProductCard from '../components/common/ProductCard';
import Spinner from '../components/common/Spinner';
import './Shop.css';

const Shop = () => {
  const [searchParams] = useSearchParams();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All Category');
  const [sortBy, setSortBy] = useState('default');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch categories from backend
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8020/categories');
        setCategories(response.data);
      } catch (err) {
        console.error('Error fetching categories:', err);
        setError('Failed to load categories');
      }
    };
    fetchCategories();
  }, []);

  // Fetch products when component mounts or category changes
  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      setError('');
      try {
        let url = 'http://127.0.0.1:8020/products';
        
        // If a specific category is selected, filter by it
        if (selectedCategory && selectedCategory !== 'All Category') {
          url += `?category_name=${encodeURIComponent(selectedCategory)}`;
        }
        
        const response = await axios.get(url);
        setProducts(response.data);
        setFilteredProducts(response.data);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError('Failed to load products');
        setProducts([]);
        setFilteredProducts([]);
      } finally {
        setLoading(false);
      }
    };
    
    fetchProducts();
  }, [selectedCategory]);

  // Handle category from URL params
  useEffect(() => {
    const category = searchParams.get('category');
    if (category) {
      setSelectedCategory(category);
    }
  }, [searchParams]);

  // Apply filters and sorting to products
  useEffect(() => {
    let filtered = [...products];

    // Apply sorting
    if (sortBy === 'name') {
      filtered.sort((a, b) => a.product_name.localeCompare(b.product_name));
    }

    setFilteredProducts(filtered);
  }, [products, sortBy]);

  return (
    <div className="shop-page">
      {/* Page Header */}
      <div className="container-fluid page-header py-5" style={{
        backgroundImage: 'url(/img/carousel-1.jpg)',
        backgroundSize: '200%',
        backgroundPosition: 'center 15%',
        backgroundRepeat: 'no-repeat',
        position: 'relative'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          zIndex: 1
        }}></div>
        <div className="container text-center py-5" style={{ position: 'relative', zIndex: 2 }}>
          <h1 className="display-4 text-white mb-3" style={{ fontWeight: '700' }}>Shop</h1>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb justify-content-center mb-0">
              <li className="breadcrumb-item"><a href="/">Home</a></li>
              <li className="breadcrumb-item active text-white">Shop</li>
            </ol>
          </nav>
        </div>
      </div>

      {/* Shop Content */}
      <div className="container-fluid py-5">
        <div className="container py-5">
          <div className="row g-4">
            {/* Sidebar Filters */}
            <div className="col-lg-3">
              <div className="shop-sidebar">
                {/* Categories */}
                <div className="sidebar-widget mb-4">
                  <h4 className="mb-3">Categories</h4>
                  <ul className="list-unstyled">
                    <li className="mb-2">
                      <button
                        className={`btn btn-link text-decoration-none ${
                          selectedCategory === 'All Category' ? 'text-primary fw-bold' : 'text-dark'
                        }`}
                        onClick={() => setSelectedCategory('All Category')}
                      >
                        All Category
                      </button>
                    </li>
                    {categories.map((category) => (
                      <li key={category.category_id} className="mb-2">
                        <button
                          className={`btn btn-link text-decoration-none ${
                            selectedCategory === category.category_name ? 'text-primary fw-bold' : 'text-dark'
                          }`}
                          onClick={() => setSelectedCategory(category.category_name)}
                        >
                          {category.category_name}
                        </button>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Price Filter */}
                <div className="sidebar-widget mb-4">
                  <h4 className="mb-3">Price Range</h4>
                  <div className="price-range">
                    <input
                      type="range"
                      className="form-range"
                      min="0"
                      max="2500"
                      step="50"
                      value={priceRange[1]}
                      onChange={(e) => setPriceRange([0, parseInt(e.target.value)])}
                    />
                    <div className="d-flex justify-content-between mt-2">
                      <span>${priceRange[0]}</span>
                      <span>${priceRange[1]}</span>
                    </div>
                  </div>
                </div>

                {/* Featured Product */}
                <div className="sidebar-widget">
                  <h4 className="mb-3">Featured</h4>
                  <div className="border rounded p-3">
                    <img src="/img/product-1.png" className="img-fluid mb-3" alt="Featured" />
                    <h6>Apple iPad Mini</h6>
                    <p className="text-primary mb-0">$1,050.00</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Products Grid */}
            <div className="col-lg-9">
              {/* Toolbar */}
              <div className="shop-toolbar d-flex justify-content-between align-items-center mb-4 p-3 bg-light rounded">
                <div>
                  <span className="text-muted">
                    Showing {filteredProducts.length} products
                  </span>
                </div>
                <div className="d-flex align-items-center">
                  <label className="me-2 mb-0">Sort by:</label>
                  <select
                    className="form-select"
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    style={{ width: 'auto' }}
                  >
                    <option value="default">Default</option>
                    <option value="name">Name</option>
                    <option value="price-low">Price: Low to High</option>
                    <option value="price-high">Price: High to Low</option>
                  </select>
                </div>
              </div>

              {/* Loading State */}
              {loading && (
                <div className="text-center py-5">
                  <Spinner />
                </div>
              )}

              {/* Error State */}
              {error && (
                <div className="alert alert-danger">
                  {error}
                </div>
              )}

              {/* Products Grid */}
              {!loading && !error && (
                <div className="row g-4">
                  {filteredProducts.length > 0 ? (
                    filteredProducts.map((product) => (
                      <div key={product.product_id} className="col-md-6 col-xl-4">
                        <ProductCard product={product} />
                      </div>
                    ))
                  ) : (
                    <div className="col-12">
                      <div className="alert alert-info text-center">
                        No products found matching your criteria.
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Shop;
