/**
 * ProductCard Component
 * Reusable product card for displaying product information
 */
import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const { addToCart, addToWishlist } = useCart();

  const handleAddToCart = (e) => {
    e.preventDefault();
    addToCart(product);
  };

  const handleAddToWishlist = (e) => {
    e.preventDefault();
    addToWishlist(product);
  };

  return (
    <div className="product-card">
      <div className="product-item border h-100">
        <div className="position-relative">
          <img
            src={product.image_url || '/img/product-1.png'}
            className="img-fluid w-100"
            alt={product.product_name}
            style={{ height: '250px', objectFit: 'cover' }}
          />
          {product.discount && (
            <div className="product-badge">
              <span className="badge bg-danger">{product.discount}% OFF</span>
            </div>
          )}
          <div className="product-overlay">
            <Link
              to={`/single/${product.product_id}`}
              className="btn btn-sm btn-primary rounded-circle"
            >
              <i className="fa fa-eye"></i>
            </Link>
          </div>
        </div>

        <div className="product-content p-4">
          {product.category && (
            <Link to={`/shop?category=${product.category.category_name}`} className="d-block mb-2 text-muted">
              {product.category.category_name}
            </Link>
          )}
          <Link to={`/single/${product.product_id}`} className="h5 d-block mb-3">
            {product.product_name}
          </Link>

          {product.description && (
            <p className="text-muted small mb-3" style={{ 
              overflow: 'hidden', 
              textOverflow: 'ellipsis', 
              display: '-webkit-box', 
              WebkitLineClamp: 2, 
              WebkitBoxOrient: 'vertical' 
            }}>
              {product.description}
            </p>
          )}

          <div className="d-flex justify-content-between align-items-center mb-3">
            <div>
              <span className="text-primary fw-bold fs-5">
                ${parseFloat(product.price).toFixed(2)}
              </span>
            </div>
            <div>
              <small className="text-muted">
                {product.stock_quantity > 0 ? (
                  <span className="text-success">In Stock ({product.stock_quantity})</span>
                ) : (
                  <span className="text-danger">Out of Stock</span>
                )}
              </small>
            </div>
          </div>

          <div className="d-flex gap-2">
            <button
              onClick={handleAddToCart}
              className="btn btn-primary rounded-pill flex-grow-1"
              disabled={product.stock_quantity === 0}
            >
              <i className="fas fa-shopping-cart me-2"></i>
              {product.stock_quantity === 0 ? 'Out of Stock' : 'Add to Cart'}
            </button>
            <button
              onClick={handleAddToWishlist}
              className="btn btn-outline-primary rounded-circle"
              title="Add to Wishlist"
            >
              <i className="fas fa-heart"></i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
