/**
 * Checkout Page Component
 * Order checkout with billing and payment information
 */
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import Spinner from '../components/common/Spinner';
import './Checkout.css';

const Checkout = () => {
  const { isAuthenticated, user } = useAuth();
  const navigate = useNavigate();
  
  const [cartData, setCartData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [processingOrder, setProcessingOrder] = useState(false);
  const [error, setError] = useState('');
  
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: user?.email || '',
    phone: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    country: '',
    paymentMethod: 'card'
  });

  // Fetch cart data
  useEffect(() => {
    const fetchCart = async () => {
      if (!isAuthenticated || !user) {
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const response = await axios.get(`http://127.0.0.1:8020/cart/${user.user_id}`);
        setCartData(response.data);
      } catch (err) {
        console.error('Error fetching cart:', err);
        setError('Failed to load cart');
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [isAuthenticated, user]);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!user) {
      alert('Please login to place an order');
      return;
    }

    setProcessingOrder(true);
    try {
      // Create order from cart
      const response = await axios.post('http://127.0.0.1:8020/orders/checkout', {
        user_id: user.user_id
      });

      const order = response.data;
      console.log('Order created:', order);

      // Show success message
      alert(`Order #${order.order_id} placed successfully! Total: $${parseFloat(order.total_amount).toFixed(2)}`);
      
      // Navigate to a success page or order details
      navigate(`/`);
      
    } catch (err) {
      console.error('Error creating order:', err);
      alert(err.response?.data?.detail || 'Failed to place order. Please try again.');
    } finally {
      setProcessingOrder(false);
    }
  };

  // Redirect if not logged in
  if (!isAuthenticated) {
    return (
      <div className="checkout-page">
        <div className="container py-5">
          <div className="text-center">
            <i className="fas fa-lock fa-5x text-muted mb-4"></i>
            <h3>Please Login to Checkout</h3>
            <p className="text-muted mb-4">You need to be logged in to place an order.</p>
            <button onClick={() => navigate('/')} className="btn btn-primary rounded-pill py-3 px-5">
              Go to Login
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="container py-5">
        <Spinner />
      </div>
    );
  }

  if (!cartData || !cartData.cart_items || cartData.cart_items.length === 0) {
    return (
      <div className="checkout-page">
        <div className="container py-5 text-center">
          <i className="fas fa-shopping-cart fa-5x text-muted mb-4"></i>
          <h3>Your cart is empty</h3>
          <p className="text-muted mb-4">Add some products before checking out.</p>
          <Link to="/shop" className="btn btn-primary rounded-pill py-3 px-5">
            Continue Shopping
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="checkout-page">
      <div className="container-fluid page-header py-5">
        <div className="container text-center py-5">
          <h1 className="display-4 text-white mb-3">Checkout</h1>
        </div>
      </div>

      <div className="container-fluid py-5">
        <div className="container py-5">
          <form onSubmit={handleSubmit}>
            <div className="row g-5">
              {/* Billing Details */}
              <div className="col-lg-8">
                <h4 className="mb-4">Billing Details</h4>
                <div className="row g-3">
                  <div className="col-md-6">
                    <label className="form-label">First Name *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="firstName"
                      value={formData.firstName}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label">Last Name *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="lastName"
                      value={formData.lastName}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label">Email *</label>
                    <input
                      type="email"
                      className="form-control"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label">Phone *</label>
                    <input
                      type="tel"
                      className="form-control"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-12">
                    <label className="form-label">Address *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label">City *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="city"
                      value={formData.city}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label">State/Province *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="state"
                      value={formData.state}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label">ZIP Code *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="zipCode"
                      value={formData.zipCode}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label">Country *</label>
                    <select
                      className="form-select"
                      name="country"
                      value={formData.country}
                      onChange={handleInputChange}
                      required
                    >
                      <option value="">Select Country</option>
                      <option value="USA">United States</option>
                      <option value="UK">United Kingdom</option>
                      <option value="Canada">Canada</option>
                      <option value="Australia">Australia</option>
                    </select>
                  </div>
                </div>

                {/* Payment Method */}
                <h4 className="mt-5 mb-4">Payment Method</h4>
                <div className="payment-methods">
                  <div className="form-check mb-3">
                    <input
                      className="form-check-input"
                      type="radio"
                      name="paymentMethod"
                      id="card"
                      value="card"
                      checked={formData.paymentMethod === 'card'}
                      onChange={handleInputChange}
                    />
                    <label className="form-check-label" htmlFor="card">
                      Credit/Debit Card
                    </label>
                  </div>
                  <div className="form-check mb-3">
                    <input
                      className="form-check-input"
                      type="radio"
                      name="paymentMethod"
                      id="paypal"
                      value="paypal"
                      checked={formData.paymentMethod === 'paypal'}
                      onChange={handleInputChange}
                    />
                    <label className="form-check-label" htmlFor="paypal">
                      PayPal
                    </label>
                  </div>
                  <div className="form-check">
                    <input
                      className="form-check-input"
                      type="radio"
                      name="paymentMethod"
                      id="cod"
                      value="cod"
                      checked={formData.paymentMethod === 'cod'}
                      onChange={handleInputChange}
                    />
                    <label className="form-check-label" htmlFor="cod">
                      Cash on Delivery
                    </label>
                  </div>
                </div>
              </div>

              {/* Order Summary */}
              <div className="col-lg-4">
                <div className="bg-light rounded p-4 sticky-top" style={{ top: '100px' }}>
                  <h4 className="mb-4">Your Order</h4>
                  <div className="order-items">
                    {cartItems.map((item) => (
                      <div key={item.id} className="d-flex justify-content-between mb-3">
                        <div>
                          <h6 className="mb-0">{item.name}</h6>
                          <small className="text-muted">Qty: {item.quantity}</small>
                        </div>
                        <strong>${(item.price * item.quantity).toFixed(2)}</strong>
                      </div>
                    ))}
                  </div>
                  <hr />
                  <div className="d-flex justify-content-between mb-2">
                    <span>Subtotal:</span>
                    <strong>${getCartTotal().toFixed(2)}</strong>
                  </div>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Shipping:</span>
                    <strong className="text-success">Free</strong>
                  </div>
                  <div className="d-flex justify-content-between mb-3">
                    <span>Tax (10%):</span>
                    <strong>${(getCartTotal() * 0.1).toFixed(2)}</strong>
                  </div>
                  <hr />
                  <div className="d-flex justify-content-between mb-4">
                    <h5>Total:</h5>
                    <h5 className="text-primary">${(getCartTotal() * 1.1).toFixed(2)}</h5>
                  </div>
                  <button type="submit" className="btn btn-primary w-100 rounded-pill py-3">
                    Place Order
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
